            assert_eq(acc, 6);
        }

        #[test]
        fn applies_function_correctly() {
            let vec: BoundedVec<u32, 4> = BoundedVec::from_array([1, 2, 3, 4]);
            let result = for_each_map(vec, |value| value * 2);
