    mod push_pop {
        use crate::collections::bounded_vec::BoundedVec;

        #[test]
        fn push_and_pop_operations() {
            let mut vec: BoundedVec<u32, 5> = BoundedVec::new();

            assert_eq(vec.len(), 0);

            vec.push(1);
            assert_eq(vec.len(), 1);
            assert_eq(vec.get(0), 1);

            vec.push(2);
            assert_eq(vec.len(), 2);
            assert_eq(vec.get(1), 2);

            let popped = vec.pop();
            assert_eq(popped, 2);
            assert_eq(vec.len(), 1);

            let popped2 = vec.pop();
            assert_eq(popped2, 1);
            assert_eq(vec.len(), 0);
        }

        #[test(should_fail_with = "push out of bounds")]
        fn push_to_full_vector() {
            let mut vec: BoundedVec<u32, 2> = BoundedVec::new();
            vec.push(1);
            vec.push(2);
            vec.push(3); // should panic
        }

        #[test(should_fail_with = "cannot pop from an empty vector")]
        fn pop_from_empty_vector() {
            let mut vec: BoundedVec<u32, 5> = BoundedVec::new();
            let _ = vec.pop(); // should panic
        }

        #[test]
        fn push_pop_cycle() {
            let mut vec: BoundedVec<u32, 3> = BoundedVec::new();

            // push to full
            vec.push(1);
            vec.push(2);
            vec.push(3);
            assert_eq(vec.len(), 3);

            // pop all
            assert_eq(vec.pop(), 3);
            assert_eq(vec.pop(), 2);
            assert_eq(vec.pop(), 1);
            assert_eq(vec.len(), 0);

            // push again
            vec.push(4);
            assert_eq(vec.len(), 1);
            assert_eq(vec.get(0), 4);
        }
    }

